import React, { useEffect } from "react";
import { useSearchParams } from "react-router-dom";
import { getWeatherDetails } from "../store/action";
import { useDispatch, useSelector } from "react-redux";

function Weather() {
  const dispatch = useDispatch();
  const { location = {}, current = {} } = useSelector(
    (state) => state.data
    );
  console.log(location,current)
  const [searchParams] = useSearchParams();
  const city = searchParams.get("city");

  useEffect(() => {
    if (city) {
      dispatch(getWeatherDetails(city));
    }
  }, [city]);
  return (
    <div className="app-bg">
      <div className="weather-card">
      <div className="header-weather">
        <i className="fa-solid fa-arrow-left"></i>
        <h2>Weather App</h2>
      </div>
      <hr></hr>
      <div>
        <figure>
            <img src={`http:${current?.condition?.icon}`}></img>
        </figure>
        <h3>{current?.temp_c}<span>&#176;</span>C</h3>
        <p>{current?.condition?.text}</p>
        <p>{location?.region},<span>{location?.country}</span></p>
      </div>
      <div>
         <>{current?.feelslike_c}</>
         <>{current?.humidity}</>
      </div>
      </div>
    </div>
  );
}

export default Weather;
